import loggingimport osclass DebugLog:    log_file_path = os.path.abspath(os.path.join(os.path.dirname(__file__), "../../debug_data/debug.log"))    @staticmethod    def setup_logger():        """macht die basic configurationen f√ºr den logging prozess"""        logging.basicConfig(            filename=DebugLog.log_file_path,            level=logging.ERROR,            format="%(asctime)s - %(levelname)s - %(message)s"        )    @staticmethod    def log_error(error: Exception, context: str = ""):        """loggt"""        DebugLog.setup_logger()        message = f"[{context}] Fehler: {str(error)}"        logging.error(message) # speichert die message im log file        print(message)  # Optional: zum schnellen Sehen im Terminal